import React, { useState } from 'react';
import { ArrowLeft } from 'lucide-react';

const OperatingSystemsContentPage = () => {
  const [currentModule, setCurrentModule] = useState(4);
  const [currentSubsection, setCurrentSubsection] = useState("4.2");
  
  // Course data
  const courseName = "Introduction to Operating Systems";
  const chapters = [
    {
      id: 1,
      title: "What is an Operating System?",
      duration: "1 hour",
      completed: true
    },
    {
      id: 2,
      title: "Operating System Design Issues",
      duration: "1.5 hours",
      completed: true
    },
    {
      id: 3,
      title: "Operating System Structures",
      duration: "2 hours",
      completed: true
    },
    {
      id: 4,
      title: "Abstractions, Processes, and Resources",
      duration: "2 hours",
      completed: false,
      subsections: [
        { id: "4.1", title: "Abstraction", selected: false },
        { id: "4.2", title: "Processes", selected: true },
        { id: "4.3", title: "Resources", selected: false },
        { id: "4.4", title: "Deadlocks", selected: false }
      ]
    },
    {
      id: 5,
      title: "The Influence of Security, Networking, and Multimedia",
      duration: "1.5 hours",
      completed: false
    }
  ];

  // Chapter content
  const chapterContent = {
    title: "Chapter 4: Abstractions, Processes, and Resources",
    learningObjectives: "In this chapter, you will gain a comprehensive understanding of the core concepts within an operating system, specifically focusing on abstraction, processes, resources, and deadlocks. By studying these mechanisms, you will learn how an operating system efficiently manages resources and process execution.",
    sections: [
      {
        id: "4.2",
        title: "Processes",
        content: `Processes are fundamental units of execution within an operating system, enabling users to operate applications effectively. Understanding processes involves considering several aspects, such as process states, the process control block (PCB), scheduling, and context switching.

### Process States

Processes undergo different states including ready, running, and blocked. Understanding the transitions between these states helps in identifying how the operating system schedules and manages processes:

1. **Ready State**: A process is in the ready state when it is prepared to execute as soon as it receives CPU time.
2. **Running State**: In this state, the process has been assigned CPU time and currently executes instructions.
3. **Blocked State**: A process enters a blocked state when it is waiting for some event, such as I/O operation completion.

These states form a cycle dictating the life of a process, transitioning systematically based on system resources and scheduling.

### Process Control Block (PCB)

The PCB acts as a data structure that contains critical information about the process's current state. It includes data such as process ID, process state, register contents, memory limits, and scheduling information. The OS uses this data to manage operations effectively as processes transition through different states.

### Process Scheduling

Scheduling is a fundamental operating system function that manages process execution based on a scheduling algorithm. Common algorithms include:
- **First-Come, First-Served (FCFS)**: Processes are attended in the sequence they arrive.
- **Shortest Job First (SJF)**: Prioritizes processes with the shortest execution time.
- **Round Robin (RR)**: Distributes CPU time across processes in equal time slices.`
      }
    ]
  };

  // Get current section content
  const currentSection = chapterContent.sections.find(section => section.id === currentSubsection);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '100%', margin: '0 auto' }}>
      {/* Back navigation */}
      <div style={{ padding: '12px 20px', borderBottom: '1px solid #e5e7eb' }}>
        <button style={{
          display: 'flex',
          alignItems: 'center',
          background: 'none',
          border: 'none',
          cursor: 'pointer',
          color: '#6366f1',
          padding: '0',
          fontSize: '14px'
        }}>
          <ArrowLeft size={16} style={{ marginRight: '4px' }} />
          Home
        </button>
      </div>
      
      {/* Main Content */}
      <div style={{ padding: '20px' }}>
        <h1 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px' }}>
          {courseName}
        </h1>
        
        {/* Sharing and Bloom's level */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'flex-start',
          marginBottom: '32px' 
        }}>
          <div style={{ display: 'flex', gap: '8px' }}>
            <button style={{
              padding: '8px 12px',
              border: '1px solid #d1d5db',
              borderRadius: '4px',
              background: 'white',
              cursor: 'pointer',
              fontSize: '14px'
            }}>
              Share
            </button>
            <button style={{
              padding: '8px 12px',
              border: '1px solid #d1d5db',
              borderRadius: '4px',
              background: 'white',
              cursor: 'pointer',
              fontSize: '14px'
            }}>
              ⋮
            </button>
          </div>
          
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '14px', marginBottom: '4px' }}>
              Bloom's Taxonomy Level: Evaluate
            </div>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'flex-end', 
              marginBottom: '8px' 
            }}>
              <div style={{ 
                width: '100px', 
                height: '8px', 
                backgroundColor: '#e5e7eb',
                borderRadius: '4px',
                overflow: 'hidden',
                marginRight: '8px'
              }}>
                <div style={{
                  width: '83%',
                  height: '100%',
                  backgroundColor: '#6366f1',
                  borderRadius: '4px'
                }}></div>
              </div>
              <span>5/6</span>
            </div>
            <button style={{
              padding: '8px 12px',
              width: '100%',
              backgroundColor: '#6366f1',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px'
            }}>
              Evaluate Again
            </button>
          </div>
        </div>
        
        {/* Two-column layout */}
        <div style={{ display: 'flex', gap: '24px' }}>
          {/* Sidebar with course content */}
          <div style={{ width: '280px', flexShrink: 0 }}>
            <h2 style={{ 
              fontSize: '18px', 
              fontWeight: 'bold', 
              marginBottom: '12px' 
            }}>
              Course content
            </h2>
            
            <div style={{ marginBottom: '16px', fontSize: '14px' }}>
              12 / 20 sections completed
            </div>
            
            {/* Chapter list */}
            <div>
              {chapters.map(chapter => (
                <div key={chapter.id} style={{ marginBottom: '1px' }}>
                  <div 
                    style={{
                      padding: '12px',
                      border: '1px solid #e5e7eb',
                      backgroundColor: 'white',
                      display: 'flex',
                      alignItems: 'flex-start',
                      cursor: 'pointer'
                    }}
                  >
                    <input 
                      type="checkbox" 
                      checked={chapter.completed}
                      readOnly
                      style={{ marginRight: '8px', marginTop: '2px' }}
                    />
                    <div style={{ flex: 1 }}>
                      <div style={{ fontSize: '14px' }}>
                        Chapter {chapter.id}: {chapter.title}
                      </div>
                      <div style={{ fontSize: '13px', color: '#6b7280', marginTop: '4px' }}>
                        {chapter.duration}
                      </div>
                    </div>
                    {chapter.subsections && (
                      <div style={{ marginLeft: '8px' }}>
                        {chapter.id === currentModule ? '▾' : '▸'}
                      </div>
                    )}
                  </div>
                  
                  {chapter.id === currentModule && chapter.subsections && (
                    <div>
                      {chapter.subsections.map(subsection => (
                        <div 
                          key={subsection.id}
                          style={{
                            padding: '12px 12px 12px 32px',
                            border: '1px solid #e5e7eb',
                            borderTop: 'none',
                            backgroundColor: subsection.selected ? '#eef2ff' : 'white',
                            display: 'flex',
                            alignItems: 'center',
                            cursor: 'pointer'
                          }}
                        >
                          <input 
                            type="radio"
                            checked={subsection.selected}
                            readOnly
                            style={{ marginRight: '8px' }}
                          />
                          <div style={{ fontSize: '14px' }}>
                            {subsection.id} {subsection.title}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
          
          {/* Main content */}
          <div style={{ flex: 1 }}>
            <h2 style={{ 
              fontSize: '24px', 
              fontWeight: 'bold', 
              marginBottom: '24px' 
            }}>
              {chapterContent.title}
            </h2>
            
            <div style={{ marginBottom: '24px' }}>
              <h3 style={{ 
                fontSize: '20px', 
                fontWeight: 'bold', 
                marginBottom: '16px' 
              }}>
                Learning Objectives
              </h3>
              <p style={{ lineHeight: '1.5' }}>
                {chapterContent.learningObjectives}
              </p>
            </div>
            
            {currentSection && (
              <div>
                <h3 style={{ 
                  fontSize: '20px', 
                  fontWeight: 'bold', 
                  marginBottom: '16px' 
                }}>
                  {currentSection.id} {currentSection.title}
                </h3>
                <div 
                  style={{ lineHeight: '1.6' }}
                  dangerouslySetInnerHTML={{ __html: currentSection.content.replace(/\n\n/g, '<br/><br/>').replace(/###\s(.*)/g, '<h4 style="font-size: 18px; font-weight: bold; margin-top: 20px; margin-bottom: 12px;">$1</h4>').replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') }}
                />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default OperatingSystemsContentPage;
