import React, { useState } from 'react';
import { 
  ChevronDown, 
  ChevronUp, 
  CheckCircle, 
  Circle, 
  ArrowLeft, 
  Share2, 
  MoreVertical, 
  ExternalLink,
  Home,
  BookOpen,
  Award,
  Brain,
  RefreshCw,
  MessageCircle,
  ChevronRight
} from 'lucide-react';

const EmbeddedSystemsLearningPage = () => {
  // State for tracking current module and chapter
  const [currentModule, setCurrentModule] = useState(5);
  const [currentSubchapter, setCurrentSubchapter] = useState("5.4");
  const [expandedModules, setExpandedModules] = useState([5]);
  const [showChatbot, setShowChatbot] = useState(false);
  
  // Bloom's Taxonomy level (0-7)
  const bloomsLevel = 5; // Example level
  
  // Course data based on content.md
  const courseName = "Introduction to Embedded Systems";
  const modules = [
    {
      id: 1,
      title: "What are Embedded Systems?",
      totalTime: "1 hour",
      completed: true,
      subchapters: [
        { id: "1.1", title: "Definition and Characteristics of Embedded Systems", completed: true },
        { id: "1.2", title: "Distinguishing Features", completed: true },
        { id: "1.3", title: "Examples of Embedded Systems Across Various Domains", completed: true },
        { id: "1.4", title: "Applications and Impact on Society", completed: true }
      ]
    },
    {
      id: 2,
      title: "Design Challenges in Embedded Systems",
      totalTime: "1 hour",
      completed: true,
      subchapters: [
        { id: "2.1", title: "Resource Limitations", completed: true },
        { id: "2.2", title: "Real-time Constraints and Scheduling", completed: true },
        { id: "2.3", title: "Power Consumption and Energy Efficiency", completed: true },
        { id: "2.4", title: "Reliability and Fault Tolerance", completed: true },
        { id: "2.5", title: "Cost and Time-to-Market Considerations", completed: true }
      ]
    },
    {
      id: 3,
      title: "Embedded Processor Technology",
      totalTime: "1 hour",
      completed: true,
      subchapters: [
        { id: "3.1", title: "Introduction to Microprocessors and Microcontrollers", completed: true },
        { id: "3.2", title: "RISC vs. CISC Architectures", completed: true },
        { id: "3.3", title: "Key Processor Characteristics", completed: true },
        { id: "3.4", title: "Selection Criteria for Embedded Processors", completed: true }
      ]
    },
    {
      id: 4,
      title: "Hardware Design Fundamentals",
      totalTime: "1 hour",
      completed: true,
      subchapters: [
        { id: "4.1", title: "Basic Digital Logic Gates and Their Functions", completed: true },
        { id: "4.2", title: "Input/Output Interfaces", completed: true },
        { id: "4.3", title: "Memory Organization", completed: true },
        { id: "4.4", title: "Clock Systems and Timing Considerations", completed: true },
        { id: "4.5", title: "Simple Hardware Design Examples", completed: true }
      ]
    },
    {
      id: 5,
      title: "Microcontroller Architectures",
      totalTime: "1 hour",
      completed: false,
      subchapters: [
        { id: "5.1", title: "8051 Architecture", completed: false },
        { id: "5.2", title: "PIC Architecture", completed: false },
        { id: "5.3", title: "ARM Architecture", completed: false },
        { id: "5.4", title: "Comparison of the Three Architectures", completed: false }
      ]
    }
  ];

  // Chapter content (based on content.md)
  const chapterContent = `# Chapter 5: Microcontroller Architectures (8051, PIC, ARM)

## Learning Objectives
In this chapter, students will gain insights into three widely used microcontroller architectures: 8051, PIC, and ARM. By the end of this section, students will be equipped to compare their features and capabilities, serving as a foundation for more detailed exploration in subsequent modules. This chapter builds on Chapters 3 and 4 by providing concrete examples of microcontroller architectures and explaining their practical applications and performance metrics.

## 5.1 8051 Architecture

### Overview
The 8051 microcontroller has been a significant player in the embedded system environment since the early 1980s. Developed by Intel, it offers a robust architecture that has become the backbone of many embedded systems, primarily due to its simplicity and flexibility. 

### Registers
The 8051 has a diverse set of registers, playing critical roles in executing instructions efficiently. It comprises the following:
- **Accumulator (ACC)**: Essential for arithmetic operations.
- **B Register**: Utilized in conjunction with the accumulator for multiplication and division operations.
- **Program Counter (PC)**: Points to the next instruction to be executed.
- **Stack Pointer (SP)**: Serves in stack operations, facilitating data storage during interrupts.

### Memory Map
The memory architecture of the 8051 is divided into Program Memory and Data Memory. 
- The **Program Memory** (often read-only) stores the code, while the Data Memory (RAM) stores interim data variables and stack data. The 8051 can address 64 KB of program memory space and 256 bytes of data memory, segmented further into internal and external data memory.

### Instruction Set
The 8051 microcontroller's instruction set is optimized for highly efficient real-time manipulation and control-oriented applications, containing a mix of byte and bit-level instructions for efficient processing. This expands its usability in operations requiring quick and reliable execution, significant in critical time-based applications.

For more information, refer to [8051 Memory Organization - Annamalai University (PDF)](https://www.annamalaiuniversity.ac.in/studport/download/engg/csd/resources/8051%20Memory%20Organization.pdf) and [8051 Instruction Set - Silicon Labs (PDF)](https://www.silabs.com/documents/public/presentations/8051_Instruction_Set.pdf).

## 5.2 PIC Architecture

### Overview
PIC microcontrollers, produced by Microchip Technology, are renowned for their performance and ease of use in various applications like automotive, industrial, and consumer electronics.

### Registers
PIC's enhanced architecture includes a large register set. The General Purpose Registers (GPRs) facilitate rapid data processing, supported by the Working Register which acts as an accumulator.

### Memory Map
The architecture allows for flexible, complex memory usage:
- **Program Memory**: Typically holding non-volatile instructions.
- **Data Memory (RAM)**: Used for volatile data storage - notably beneficial in applications needing quick and temporary data retrieval.

### Instruction Set
The PIC instruction set architecture promotes reduced instruction cycle time while ensuring flexibility. The reduced instruction set computing (RISC) nature contributes to its efficiency in handling complex operations swiftly, critical in high-speed automation scenarios.

More details can be accessed via the [Overview of PIC 18, memory organisation, CPU, registers, pipelining (PDF)](https://infonics.files.wordpress.com/2015/03/introduction-to-pic-18-microcontrollers1.pdf).

## 5.3 ARM Architecture

### Overview
ARM architecture stands out with modern, efficient processing capabilities, extensively used in smartphones, tablets, and a gamut of embedded applications due to its low power consumption and high performance.

### Registers
ARM processors feature a more sophisticated register structure, typically containing 16 to 31 general-purpose registers, enhancing data handling capabilities and processing power.

### Memory Map
ARM architecture employs a flat memory model supporting various memory types (e.g., cache, RAM), optimizing system performance.

### Instruction Set
With distinct sets like the ARM instruction set and the Thumb instruction set, ARM offers flexibility for developers, balancing performance with power efficiency, crucial in mobile and wearable technology production.

For comprehensive instruction details, check out [ARM Instruction Set (PDF)](https://iitd-plos.github.io/col718/ref/arm-instructionset.pdf).

## 5.4 Comparison of the Three Architectures

### Performance and Power
- **8051**: Low power consumption is optimal for basic applications.
- **PIC**: Moderate in power use, balancing performance and energy conservation well.
- **ARM**: Superior performance; design facilitates high computational tasks with energy efficiency.

### Application Suitability
- **8051**: Simple control tasks.
- **PIC**: Versatile applications from simple to moderately complex tasks.
- **ARM**: High-demand applications requiring robust processing.

### Summary
Each architecture carries unique strengths, serving various market needs and requirements. ARM stands out for complex applications, PIC balances flexibility and performance, while 8051 serves as a sturdy choice for simpler applications.

Additional insights can be gathered from [Difference between AVR, ARM, 8051, and PIC Microcontrollers - Elprocus](https://www.elprocus.com/difference-between-avr-arm-8051-and-pic-microcontroller/).

For further in-depth exploration, students are encouraged to visit the suggested links and expand their understanding through practice and further reading.`;

  // Find current chapter details
  const activeModule = modules.find(m => m.id === currentModule);

  // Toggle module expansion
  const toggleModule = (moduleId: number) => {
    if (expandedModules.includes(moduleId)) {
      setExpandedModules(expandedModules.filter(id => id !== moduleId));
    } else {
      setExpandedModules([...expandedModules, moduleId]);
    }
  };

  // Set current chapter and subchapter
  const selectModule = (moduleId: number) => {
    setCurrentModule(moduleId);
    if (!expandedModules.includes(moduleId)) {
      setExpandedModules([...expandedModules, moduleId]);
    }
  };

  const selectSubchapter = (moduleId: number, subchapterId: string) => {
    setCurrentModule(moduleId);
    setCurrentSubchapter(subchapterId);
  };

  // Calculate progress
  const totalSubchapters = modules.reduce((acc, module) => acc + module.subchapters.length, 0);
  const completedSubchapters = modules.reduce((acc, module) => acc + module.subchapters.filter(c => c.completed).length, 0);
  const progressPercentage = Math.round((completedSubchapters / totalSubchapters) * 100);
  
  // Function to parse and render markdown-like content
  const renderContent = (content: string) => {
    if (!content) return null;
    
    const lines = content.split('\n');
    return lines.map((line, index) => {
      // Headers
      if (line.startsWith('# ')) {
        return <h1 key={index} className="text-3xl font-bold mt-8 mb-4">{line.substring(2)}</h1>;
      }
      if (line.startsWith('## ')) {
        return <h2 key={index} className="text-2xl font-semibold mt-6 mb-3">{line.substring(3)}</h2>;
      }
      if (line.startsWith('### ')) {
        return <h3 key={index} className="text-xl font-semibold mt-5 mb-2">{line.substring(4)}</h3>;
      }
      
      // Lists
      if (line.startsWith('- ')) {
        return <li key={index} className="ml-6 mb-1">{line.substring(2)}</li>;
      }
      
      // Bold text
      if (line.includes('**')) {
        const parts = line.split('**');
        return (
          <p key={index} className="mb-3">
            {parts.map((part: string, i: number) => 
              i % 2 === 0 ? part : <strong key={i}>{part}</strong>
            )}
          </p>
        );
      }
      
      // Links
      if (line.includes('[') && line.includes('](')) {
        const linkText = line.substring(
          line.indexOf('[') + 1,
          line.indexOf(']')
        );
        const linkUrl = line.substring(
          line.indexOf('(') + 1,
          line.indexOf(')')
        );
        return (
          <p key={index} className="mb-3">
            <a href={linkUrl} className="text-blue-600 hover:underline flex items-center" target="_blank" rel="noopener noreferrer">
              {linkText} <ExternalLink size={14} className="ml-1" />
            </a>
          </p>
        );
      }
      
      // Regular paragraph
      if (line.trim() !== '') {
        return <p key={index} className="mb-3">{line}</p>;
      }
      
      return <br key={index} />;
    });
  };
  
  // Bloom's Taxonomy levels
  const bloomsTaxonomyLevels = [
    { name: "Remember", color: "#6366F1" }, // Indigo
    { name: "Understand", color: "#8B5CF6" }, // Violet
    { name: "Apply", color: "#EC4899" }, // Pink
    { name: "Analyze", color: "#F97316" }, // Orange
    { name: "Evaluate", color: "#EF4444" }, // Red
    { name: "Create", color: "#06B6D4" }  // Cyan
  ];

  // Get content specifically for current subchapter
  const getCurrentSubchapterContent = () => {
    if (!currentSubchapter) return null;
    
    const contentLines = chapterContent.split('\n');
    let collecting = false;
    let result = [];
    let currentSection = '';
    
    for (const line of contentLines) {
      // Check if this is a section header for the current subchapter
      if (line.startsWith('## ' + currentSubchapter.split('.')[0] + '.' + currentSubchapter.split('.')[1])) {
        collecting = true;
        currentSection = currentSubchapter;
        result.push(line);
      } 
      // Check if this is the start of the next section
      else if (line.startsWith('## ') && collecting) {
        collecting = false;
      }
      // Otherwise if we're collecting, add the line
      else if (collecting) {
        result.push(line);
      }
    }
    
    return result.join('\n');
  };

  return (
    <div className="flex flex-col min-h-screen bg-white">
      {/* Top navigation */}
      <header className="flex justify-between items-center px-4 py-3 bg-white border-b border-gray-200">
        <div className="flex items-center">
          <button 
            className="mr-4 text-gray-700 hover:text-gray-900 flex items-center"
          >
            <ArrowLeft size={20} className="mr-1" />
            <Home size={20} />
          </button>
          <h1 className="text-lg font-medium text-gray-900 truncate max-w-lg">{courseName}</h1>
        </div>
        <div className="flex items-center space-x-3">
          <button className="flex items-center text-sm font-medium text-gray-700 hover:text-gray-900">
            <Share2 size={18} className="mr-1" />
            Share
          </button>
          <button className="text-gray-700 hover:text-gray-900">
            <MoreVertical size={20} />
          </button>
        </div>
      </header>
      
      {/* Bloom's Taxonomy Progress Bar */}
      <div className="px-4 py-3 bg-gray-50 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <Brain size={20} className="mr-2 text-indigo-600" />
            <span className="text-sm font-medium text-gray-700">Bloom's Taxonomy Level:</span>
            <div className="flex items-center ml-3">
              <span className="text-sm font-medium text-indigo-600 mr-2">{bloomsTaxonomyLevels[bloomsLevel-1]?.name || 'N/A'}</span>
              <div className="w-32 h-2 bg-gray-200 rounded-full overflow-hidden">
                <div 
                  className="h-full" 
                  style={{ 
                    width: `${(bloomsLevel / 6) * 100}%`,
                    backgroundColor: bloomsTaxonomyLevels[bloomsLevel-1]?.color || '#6366F1'
                  }} 
                />
              </div>
              <span className="text-xs text-gray-500 ml-2">{bloomsLevel}/6</span>
            </div>
          </div>
          <button className="flex items-center text-sm font-medium text-indigo-600 hover:text-indigo-800 px-4 py-1 border border-indigo-600 rounded-md">
            <RefreshCw size={16} className="mr-1" />
            Evaluate Again
          </button>
        </div>
      </div>

      {/* Main content with sidebar */}
      <div className="flex flex-1 overflow-hidden">
        {/* Left sidebar */}
        <aside className="w-80 bg-gray-50 border-r border-gray-200 flex flex-col overflow-hidden">
          <div className="p-4 border-b border-gray-200">
            <h2 className="text-lg font-medium text-gray-900">Course content</h2>
            <div className="mt-2 flex items-center text-sm text-gray-600">
              <span>{completedSubchapters} / {totalSubchapters} sections completed</span>
              <div className="ml-2 w-24 h-2 bg-gray-200 rounded-full overflow-hidden">
                <div 
                  className="h-full bg-green-500" 
                  style={{ width: `${progressPercentage}%` }} 
                />
              </div>
            </div>
          </div>
          <div className="flex-1 overflow-y-auto">
            {modules.map(module => (
              <div key={module.id} className="border-b border-gray-200">
                <button 
                  className={`flex items-center justify-between w-full p-4 text-left hover:bg-gray-100 transition-colors ${
                    currentModule === module.id ? 'bg-gray-100' : ''
                  }`}
                  onClick={() => toggleModule(module.id)}
                >
                  <div className="flex items-center space-x-2">
                    {module.completed ? (
                      <CheckCircle size={20} className="text-green-500 flex-shrink-0" />
                    ) : (
                      <Circle size={20} className="text-gray-400 flex-shrink-0" />
                    )}
                    <span className="text-sm font-medium text-gray-900">{module.title}</span>
                  </div>
                  <div className="flex items-center">
                    <span className="text-xs text-gray-500 mr-2">{module.totalTime}</span>
                    {expandedModules.includes(module.id) ? (
                      <ChevronUp size={18} className="text-gray-500" />
                    ) : (
                      <ChevronDown size={18} className="text-gray-500" />
                    )}
                  </div>
                </button>
                
                {expandedModules.includes(module.id) && (
                  <div className="bg-gray-50 pl-10 pr-4 py-2">
                    {module.subchapters.map((subchapter) => (
                      <div 
                        key={subchapter.id}
                        className="flex items-start py-2 cursor-pointer"
                        onClick={() => selectSubchapter(module.id, subchapter.id)}
                      >
                        <div className="flex items-center mr-2 mt-0.5">
                          {subchapter.completed ? (
                            <CheckCircle size={16} className="text-green-500 flex-shrink-0" />
                          ) : currentSubchapter === subchapter.id ? (
                            <BookOpen size={16} className="text-blue-500 flex-shrink-0" />
                          ) : (
                            <Circle size={16} className="text-gray-400 flex-shrink-0" />
                          )}
                        </div>
                        <span className={`text-sm ${currentSubchapter === subchapter.id ? 'text-blue-600 font-medium' : 'text-gray-700'}`}>
                          {subchapter.id} {subchapter.title}
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        </aside>

        {/* Main content area */}
        <main className="flex-1 overflow-auto">
          <div className="p-8 max-w-3xl mx-auto">
            {/* Learning path breadcrumb */}
            <div className="flex items-center text-sm text-gray-500 mb-6">
              <span>Module 1</span>
              <ChevronRight size={16} className="mx-1" />
              <span className="font-medium text-gray-700">{activeModule?.title}</span>
            </div>
            
            {/* Render the chapter content */}
            <div className="prose prose-indigo max-w-none">
              {currentSubchapter === "5.4" 
                ? renderContent(chapterContent) 
                : renderContent(getCurrentSubchapterContent())}
              
              {/* Example architecture images */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 my-8">
                <div className="border border-gray-200 rounded-lg overflow-hidden">
                  <img src="/api/placeholder/400/250" alt="8051 Architecture" className="w-full h-auto" />
                  <div className="p-3 text-center text-sm font-medium">8051 Architecture</div>
                </div>
                <div className="border border-gray-200 rounded-lg overflow-hidden">
                  <img src="/api/placeholder/400/250" alt="PIC Architecture" className="w-full h-auto" />
                  <div className="p-3 text-center text-sm font-medium">PIC Architecture</div>
                </div>
                <div className="border border-gray-200 rounded-lg overflow-hidden">
                  <img src="/api/placeholder/400/250" alt="ARM Architecture" className="w-full h-auto" />
                  <div className="p-3 text-center text-sm font-medium">ARM Architecture</div>
                </div>
              </div>
              
              {/* Study resources */}
              <div className="mt-12 bg-gray-50 border border-gray-200 rounded-lg p-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
                  <Award size={20} className="mr-2 text-indigo-600" />
                  Additional Learning Resources
                </h3>
                <ul className="space-y-3">
                  <li>
                    <a href="https://www.annamalaiuniversity.ac.in/studport/download/engg/csd/resources/8051%20Memory%20Organization.pdf" className="flex items-center text-blue-600 hover:text-blue-800">
                      <ExternalLink size={16} className="mr-2" />
                      <span>8051 Memory Organization - Annamalai University</span>
                    </a>
                  </li>
                  <li>
                    <a href="https://www.silabs.com/documents/public/presentations/8051_Instruction_Set.pdf" className="flex items-center text-blue-600 hover:text-blue-800">
                      <ExternalLink size={16} className="mr-2" />
                      <span>8051 Instruction Set - Silicon Labs</span>
                    </a>
                  </li>
                  <li>
                    <a href="https://infonics.files.wordpress.com/2015/03/introduction-to-pic-18-microcontrollers1.pdf" className="flex items-center text-blue-600 hover:text-blue-800">
                      <ExternalLink size={16} className="mr-2" />
                      <span>Introduction to PIC 18 Microcontrollers</span>
                    </a>
                  </li>
                  <li>
                    <a href="https://iitd-plos.github.io/col718/ref/arm-instructionset.pdf" className="flex items-center text-blue-600 hover:text-blue-800">
                      <ExternalLink size={16} className="mr-2" />
                      <span>ARM Instruction Set Documentation</span>
                    </a>
                  </li>
                  <li>
                    <a href="https://www.elprocus.com/difference-between-avr-arm-8051-and-pic-microcontroller/" className="flex items-center text-blue-600 hover:text-blue-800">
                      <ExternalLink size={16} className="mr-2" />
                      <span>Comparison of Microcontroller Architectures - Elprocus</span>
                    </a>
                  </li>
                </ul>
              </div>
              
              {/* Navigation buttons */}
              <div className="mt-12 flex justify-between">
                <button 
                  className="flex items-center px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                  onClick={() => currentModule > 1 && setCurrentModule(currentModule - 1)}
                  disabled={currentModule <= 1}
                >
                  <ArrowLeft size={16} className="mr-2" />
                  Previous Chapter
                </button>
                <button 
                  className="flex items-center px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700"
                  onClick={() => currentModule < modules.length && setCurrentModule(currentModule + 1)}
                  disabled={currentModule >= modules.length}
                >
                  Next Chapter
                  <ChevronRight size={16} className="ml-2" />
                </button>
              </div>
            </div>
          </div>
        </main>
      </div>
      
      {/* Chatbot button */}
      <div className="fixed bottom-6 right-6">
        <button 
          className="w-14 h-14 rounded-full bg-indigo-600 text-white flex items-center justify-center shadow-lg hover:bg-indigo-700 transition-colors"
          onClick={() => setShowChatbot(!showChatbot)}
        >
          <MessageCircle size={24} />
        </button>
      </div>
      
      {/* Chatbot panel */}
      {showChatbot && (
        <div className="fixed bottom-24 right-6 w-80 h-96 bg-white rounded-lg shadow-xl border border-gray-200 flex flex-col overflow-hidden">
          <div className="p-4 bg-indigo-600 text-white flex justify-between items-center">
            <span className="font-medium">Learning Assistant</span>
            <button 
              className="text-white hover:text-gray-200"
              onClick={() => setShowChatbot(false)}
            >
              <X size={18} />
            </button>
          </div>
          <div className="flex-1 p-4 overflow-y-auto bg-gray-50">
            <div className="bg-indigo-100 p-3 rounded-lg rounded-tl-none mb-3 max-w-xs">
              Hi there! How can I help you with Chapter 5: Microcontroller Architectures?
            </div>
          </div>
          <div className="p-3 border-t border-gray-200 flex">
            <input 
              type="text"
              placeholder="Ask a question..."
              className="flex-1 border border-gray-300 rounded-l-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            />
            <button className="bg-indigo-600 text-white px-3 py-2 rounded-r-md hover:bg-indigo-700">
              <Send size={18} />
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

// Helper components for icons that aren't exported from lucide-react
const X = (props) => (
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width={props.size || 24} 
    height={props.size || 24} 
    viewBox="0 0 24 24" 
    fill="none" 
    stroke="currentColor" 
    strokeWidth="2" 
    strokeLinecap="round" 
    strokeLinejoin="round" 
    className={props.className}
  >
    <line x1="18" y1="6" x2="6" y2="18" />
    <line x1="6" y1="6" x2="18" y2="18" />
  </svg>
);

const Send = (props) => (
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width={props.size || 24} 
    height={props.size || 24} 
    viewBox="0 0 24 24" 
    fill="none" 
    stroke="currentColor" 
    strokeWidth="2" 
    strokeLinecap="round" 
    strokeLinejoin="round" 
    className={props.className}
  >
    <line x1="22" y1="2" x2="11" y2="13" />
    <polygon points="22 2 15 22 11 13 2 9 22 2" />
  </svg>
);

export default EmbeddedSystemsLearningPage;
